$(document).ready(function(){
    var codemirror = $('.CodeMirror')[0].CodeMirror;

    function autoprefix(css){
        var result = autoprefixer().process(css);
        return result.css;
    }

    function beautify(css){
        return cssbeautify(css, {autosemicolon: true});
    }

    function betterTab(cm) {
        if (cm.somethingSelected()) {
            cm.indentSelection("add");
        } else {
            cm.replaceSelection(cm.getOption("indentWithTabs")? "\t":
            Array(cm.getOption("indentUnit") + 1).join(" "), "end", "+input");
        }
    }

    $(".beautify").click(function(){
        codemirror.setValue(beautify(codemirror.getValue()))
    });


    $(".autoprefix").click(function(){
        codemirror.setValue(autoprefix(codemirror.getValue()))
    });
    
    codemirror.options.tabSize = 4;
    
    codemirror.options.indentUnit = 4;
    
    codemirror.options.indentWithTabs = !1;
    
    codemirror.options.extraKeys = {
        Tab: betterTab,
        "Ctrl-S": function(instance) { $('#saveeditfile').click(); },
        "Ctrl-/": "undo"
    }
});